import React, { Component } from 'react';
import classes from './ElevenIteration.module.css';

class ElevenIteration extends Component {
    constructor(props) {
        super(props)
        this.state = {
            timer: 0,
        }
        this.resetTimer = () => {
            clearInterval(this.timerID)
            this.setState({
                timer: 0,
            })
        }
    }
    componentDidMount() {
        console.log('Компонент готов к использованию: componentDidMount')
        this.startTimer = () => {
            this.timerID = setInterval(() => {
                this.setState({
                    timer: this.state.timer + 1,
                })
            }, 1000)
        }
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log(prevState.value)
    }

    componentWillUnmount() {
        clearImmediate(this.timerID)
    }

    render() {
        return (
            <div>
                <div className={classes.ElevenIteration}>
                    <h2>{ this.state.timer }</h2>
                </div>
                <div className={classes.Buttons}>
                    <button onClick={this.startTimer}>Start</button>
                    <button onClick={this.resetTimer}>Reset</button>
                </div>
            </div>
        )
    }
}

export default ElevenIteration
