type initialStateType = {
    value: number,
}

const initialState = {
    value: 0,
}

export default function rootReducer(state:initialStateType = initialState, action:any) {
    switch(action.type) {
        case 'ADD':
            return {
                value: state.value + 1
            }
        case 'ADD12':
            return {
                value: state.value + payload.value
            }
        case 'SUB':
            return {
                value: state.value - 1
            }
        case 'SUB17':
            return {
                value: state.value - action.value
            }
        case 'RESET':
            return {
                value: 0
            }
        default:
            return state
    }

    return state
}
