import React, { ReactNode } from 'react';
import { connect } from 'react-redux'

class ReduxComponent extends React.Component<{counter?:number}, {}> {
    render() {
        console.log('REDUX_APP', this.props)
        return (
            <div style={{
                width: '300px',
                margin: '0 auto',
                textAlign: 'center',
            }}>
                <h3>{ this.props.counter }</h3>
                <button>Плюс 1</button>
                <button>Минус 1</button>
                <button>Сбросить</button>
            </div> 
        )
    }
}

function mapStateToProps(state:object) {
    console.log(state)
    return {
        counter: state.value
    }
}

export default connect(mapStateToProps)(ReduxComponent)
