import React, {Component} from 'react';
import {Route} from "react-router-dom";
import './App.css';
import Header from './Components/Header/Header';
import Footer from './Components/Footer/Footer';
import Main from './Components/Main/Main';
import News from './Components/News/News';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showNews: false,
      news: [
        {title: 'Новость 1', content: 'Содержание новости 1'},
        {title: 'Новость 2', content: 'Содержание новости 2'},
        {title: 'Новость 3', content: 'Содержание новости 3'},
      ]
    }
  }
  showNews = () => {
    this.setState(state => {
      return !state.showNews
    })
  }
  render() {
    return(
        <React.Fragment>
          <Header/>
          <div className='wrapper'>
              <Route exact path='/' render={Main}/>
              <Route path='/news' render={() => {
                return (
                    <button onClick={this.showNews}>Показать новости</button>
                    (this.state.showNews) ?
                    <News name = {this.state.news[0].title} content = {this.state.news[0].content}/>
                    : null
                    )
              }}/>
          </div>
          <Footer/>
        </React.Fragment>
    )
  }
}

export default App;
