import React from 'react';
import { connect } from 'react-redux'

interface IProps {
    counter?: number,
    onAdd?(): void|undefined,
    onAddNumber?(): void|undefined,
    onSub?(): void|undefined,
    onReset?(): void|undefined,
}

class ReduxComponent extends React.Component<IProps, {}> {
    render() {
        console.log('REDUX_APP', this.props)
        return (
            <div style={{
                width: '300px',
                margin: '0 auto',
                textAlign: 'center',
            }}>
                <h3>{ this.props.counter }</h3>
                <div style={{
                    marginBottom: '15px'
                }}>
                    <button onClick={this.props.onAdd}>Плюс 1</button>
                    <button onClick={this.props.onSub}>Минус 1</button>
                </div>
                <div>
                    <button onClick={this.props.onAddNumber.bind(this, 12)}>Плюс 12</button>
                    <button onClick={this.props.onAddNumber?.bind()}>Минус 17</button>
                </div>
                <button onClick={this.props.onReset}>Сбросить</button>
            </div> 
        )
    }
}

function mapStateToProps(state:any) {
    console.log(state)
    return {
        counter: state.value
    }
}

function mapDispatchToProps(dispatch:any) {
    return {
        onAdd: () => dispatch({type: 'ADD'}),
        onSub: () => dispatch({type: 'SUB'}),
        onSubNumber: (number: number) => dispatch({type: 'SUB_NUMBER', value: number}),
        onReset: () => dispatch({type: 'RESET'})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ReduxComponent)
