import React from 'react';
import { connect } from 'react-redux'

class ReduxComponent extends React.Component<{}, {value: number}> {
    state = {
        value: 0
        }
    render() {
        console.log('REDUX_APP', this.props)
        return (
            <div style={{
                width: '300px',
                margin: '0 auto',
                textAlign: 'center',
            }}>
                <h3>{ this.state.value }</h3>
                <button onClick={() => {this.setState({value: this.state.value + 1})}}>Плюс 1</button>
                <button onClick={() => {this.setState({value: this.state.value - 1})}}>Минус 1</button>
                <button onClick={() => {this.setState({value: 0})}}>Сбросить</button>
            </div> 
        )
    }
}

function mapStateToProps(state:any) {
    console.log(state)
    return {
        counter: state.value
    }
}

export default connect(mapStateToProps)(ReduxComponent)
