import React, { Component } from 'react';

class ElevenIteration extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: 0,
        }
        this.up = () => {
            this.setState({
                value: this.state.value + 1,
            })
        }
        this.down = () => {
            this.setState({
                value: this.state.value - 1,
            })
        }
    }
    componentDidMount() {
        console.log('Компонент готов к использованию: componentDidMount')
    }

    componentDidUpdate(prevState) {
        console.log('Компонент обновлён: componentWillUpdate', prevState);
    }

    componentWillUnmount() {
        console.log('Компонент удалён из рендера: componentWillUnmount');
    }

    render() {
        return (
            <div>
                <h2>{ this.state.value }</h2>
                <button onClick={this.up}>+</button>
                <button onClick={this.down}>-</button>
            </div>
        )
    }
}

export default ElevenIteration
