import React, { useState, useEffect, useRef } from 'react';
import classes from "./ElevenIterationClass.module.css";

export default () => {
    const [timer, setTimer] = useState(0);
    const [countTimer, setCountTimer] = useState(true);
    const startTimer = () => {
        setCountTimer(false)
        setInterval(() => {
            setTimer(timer + 1)
        }, 100)
    }

    const stopTimer = () => {
        setTimer(timer - 1)
    }
    return (
        <div>
            <div className={classes.ElevenIteration}>
                <h2>Таймер функционального компонента</h2>
                <p>{ timer }</p>
            </div>
            <div className={classes.Buttons}>
                { countTimer ?  <button onClick={startTimer}>Start</button> : <button>Stop</button>}
                <button>Reset</button>
            </div>
        </div>
    )
}
