import React, {useState} from 'react';
import classes from "./NineIteration.module.css";

function computeInitialCounter() {
    console.log('Processing…')
    return Math.trunc(Math.random() * 20)
}

export default () => {
    // const [counterState, setCounter] = useState(computeInitialCounter());
    const [counterState, setCounter] = useState(() => computeInitialCounter());

    const [state, setState] = useState({
        title: 'Счётчик',
        date: Date.now()
    })

    function increment() {
        setCounter(prev => prev + 1)
    }

    function decrement () {
        setCounter(counterState - 1)
    }

    function updateTitle() {
        setState(prev => {
            return {
                ...prev,
                title: 'New Change'
            }
        })
    }

    return (
        <div className={classes.NineIteration}>
            <h1>Счётчик: {counterState}</h1>
            <button onClick={increment}>Вверх</button>
            <button onClick={decrement}>Вниз</button>
            <button onClick={updateTitle}>Change Title</button>

            <pre>{JSON.stringify(state, null, 2)}</pre>
        </div>
    )
}
