import React, { Component } from 'react';

class ElevenIteration extends Component {
    constructor(props) {
        super(props)
        this.state = {
            changeMod: false,
            title: '11 итерация',
        }
        this.changeMod = () => {
            this.setState({
                changeMod: !this.state.changeMod,
            })
        }
    }
    componentDidMount() {
        console.log('Компонент готов к использованию: componentDidMount')
    }

    componentDidUpdate(prevState) {
        console.log('Компонент обновлён: componentWillUpdate', prevState);
    }

    componentWillUnmount() {
        console.log('Компонент удалён из рендера: componentWillUnmount');
    }

    render() {
        return (
            <div>
                { this.state.changeMod && <input type="text" placeholder={this.state.title}/> }
                { !this.state.changeMod && <p onDoubleClick={this.changeMod}>{ this.state.title }</p> }
            </div>
        )
    }
}

export default ElevenIteration
